package com.comit.course._01_.operator;

class Relational {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		int b = 8;
		
	
		boolean equalTo =  a == b;
		boolean notEqualTo =  a != b;
		boolean greatThan =  a > b;
		boolean lessThan =  a < b;
		boolean greaterThanEqual =  a >= b;
		boolean lessThanEquaal =  a <= b;
		
		
		System.out.println("Equal To: " + equalTo);
		System.out.println("Not Equal To: " + notEqualTo);
		System.out.println("Greater Than: " + greatThan);
		System.out.println("Less Than: " + lessThan);
		System.out.println("Greater Than Equal To: " + greaterThanEqual);
		System.out.println("Less Than Equal To: " + lessThanEquaal);
	}

}

